import React, { useState } from 'react';
import { StyleSheet, Text, View, Dimensions, Image } from 'react-native';
import { TouchableHighlight } from 'react-native-gesture-handler';
import { useNavigation } from '@react-navigation/native';
import { Asset } from 'expo-asset';
import color from '../styles/colors';

function CardComponent(props) {
    const navigation = useNavigation();
    const { userid, _id, title, type, address, rooms, price, area, image } = props.appointment;
    const routeAssets = '../assets/'
    // console.log(routeAssets+image);
    // const imageURI = Asset.fromModule(require('../assets/casa1.jpg')).uri;
    return (

        <View style={styles.container}>
            <View style={styles.containerImage}>
                <Image style={styles.image} source={require('../assets/'+image)} />
                <Text style={styles.appointmentText}>{title}</Text>
                {/* <Text style={styles.text}>Type: {type}</Text> */}
                {/* <Text style={styles.text}>Address: {address}</Text>
                <Text style={styles.text}>Rooms: {rooms}</Text> */}
                <Text style={styles.text}>Price: $ {price}</Text>
                {/* <Text style={styles.text}>Area: {area}</Text>
                <Text style={styles.text}>ID: {_id}</Text> */}
                {/* <Text style={styles.text}>image: {image}</Text> */}
                <Image source={require('../assets/casa.jpg')}></Image>
            </View>
            <View style={styles.containerButtons}>
                <TouchableHighlight style={styles.updateButton} onPress={() =>
                    navigation.navigate('UpdateAppointment', {
                        itemId: _id,
                        itemTitle: title,
                        itemType: type,
                        itemAddress: address,
                        itemRooms: rooms,
                        itemArea:area,
                        itemUserid: userid
                    })}>
                    <Text style={styles.updateButtonText}>Update</Text>
                </TouchableHighlight>
                <TouchableHighlight style={styles.cancelButton} onPress={() => {
                    navigation.navigate('CancelAppointment', {
                        itemId: _id,
                        itemTitle: title,
                        itemType: type,
                        itemAddress: address,
                        itemRooms: rooms,
                        itemArea:area,
                        itemUserid: userid

                    })
                }}>
                    <Text style={styles.cancelButtonText}>Cancel</Text>
                </TouchableHighlight>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        margin: 10,
        padding: 20,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: color.Gray,
    },
    image: {
        width: 300,
        height: 200,
        borderWidth: 1,
    },
    appointmentText: {        
        fontSize: 18,
        alignItems: 'center',
        padding: 2,
        margin: 2,
        color: color.White,
    },
    text:{
        color: color.White,
    },
    containerButtons: {
        flex: 1,
        flexDirection: "row",        
        top: 10,
        // position: "absolute",
        alignItems: 'center',
        backgroundColor: 'yellow'
    },
    appointmentTitleText: {
        fontWeight: 'bold',
        fontSize: 19,
        alignItems: 'center',
        padding: 2,
        margin: 2,
        // color: color.White,
    },

    updateButton: {
        backgroundColor: '#03a9f4',
        padding: 1,
        margin: 1,
        alignItems: 'center',
        width: Dimensions.get('screen').width * 0.1
    },
    updateButtonText: {
        backgroundColor: '#03a9f4',
        padding: 1,
        margin: 1,
        alignItems: 'center',
        width: Dimensions.get('screen').width * 0.4

    },
    cancelButton: {
        backgroundColor: '#03a9f4',
        padding: 1,
        margin: 1,
        alignItems: 'center',
        width: Dimensions.get('screen').width * 0.1

    },
    cancelButtonText: {
        color: color.White,
    }

});

export default CardComponent;
